mapper.select:
SELECT a.house_mange_no AS houseMangeNo, a.house_nm AS houseNm, a.house_secd_nm AS houseSecdNm, a.si AS si, a.sigungu AS sigungu, a.rcept_bgnde AS rceptBgnde, a.rcept_endde AS rceptEndde FROM apt_table a WHERE 1=1
AND a.price >= #{homePrice.hopeMinPrice}
AND a.price <= #{homePrice.hopeMaxPrice}
AND a.si IN
#{region.si}
AND a.size IN
#{hs.size}
AND a.house_secd_nm IN
#{ht.type}

SELECT o.house_mange_no AS houseMangeNo, o.house_nm AS houseNm, o.house_secd_nm AS houseSecdNm, o.si AS si, o.sigungu AS sigungu, o.rcept_bgnde AS rceptBgnde, o.rcept_endde AS rceptEndde FROM officetel_table o WHERE 1=1
AND o.price >= #{homePrice.hopeMinPrice}
AND o.price <= #{homePrice.hopeMaxPrice}
AND o.si IN
#{region.si}
AND o.size IN
#{hs.size}
AND o.house_secd_nm IN
#{ht.type}
