mapper.select:
SELECT COUNT(*)
FROM user_favorite
WHERE users_idx   = #{usersIdx}
  AND apt_pblanc   = #{aptPblanc}

SELECT COUNT(*)
FROM user_favorite
WHERE users_idx      = #{usersIdx}
  AND office_pblanc  = #{officePblanc}

mapper.delete:
DELETE FROM user_favorite
WHERE users_idx = #{usersIdx}
  AND apt_pblanc = #{aptPblanc}

DELETE FROM user_favorite
WHERE users_idx = #{usersIdx}
  AND office_pblanc = #{officePblanc}
